import { useState, useMemo } from 'react';
import { Form, Input, Button } from "antd";
import Link from "next/link";

const LoginForm = () => {
    const [id, setId] = useState('');
    const [password, setPassword] = useState('');
    const style = useMemo(() => ({ marginTop: '10px' }), []);

  return (
    <>
      <Form style={style}>
        <Form.Item
          label="아이디"
          name="user-id"
          rules={[
            {
              required: true,
              message: "아이디를 입력해주세요!",
            },
          ]}
        >
          <Input />
        </Form.Item>
        <Form.Item
          label="비밀번호"
          name="user-password"
          rules={[
            {
              required: true,
              message: "비밀번호를 입력해주세요!",
            },
          ]}
        >
          <Input.Password />
        </Form.Item>
        <Form.Item>
          <Button type="primary" htmlType="submit">
            로그인
          </Button>
          <Link href="/signup">
            <a>
              <Button>회원가입</Button>
            </a>
          </Link>
        </Form.Item>
      </Form>
    </>
  );
};

export default LoginForm;
